import React, { Component } from 'react'
import './Map.css';
import Marker from '../Markers/Marker'
import { ReactComponent as Mapimg } from './6thFloorMap15.svg'
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Switch from '@material-ui/core/Switch';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';

export default class extends Component{
    constructor(props){
        super(props);
        this.state= {
            rooms: {},
            alarmInRooms: {},
            checked:false,
        }
        this.Timer = {};

    }
    componentDidUpdate(prevProps) {
        // Typical usage (don't forget to compare props):
        const rooms={}
        const alarm={}
        if (this.props.data !== prevProps.data) {
            this.props.data.forEach(obj => {
                rooms[obj.receiver_id] = rooms[obj.receiver_id] ? [...rooms[obj.receiver_id], obj] : [obj]
                if (rooms[obj.receiver_id].length >=2){
                    alarm[obj.receiver_id]=true
                }
            }
            )
            this.checkIn(rooms,alarm)
            this.activateAlarm(this.state.checked)
        }
    }

    checkIn=(rooms,alarmInRooms) => {
        // redundant code.
        //
        // const newAlarmState=alarmInRooms
        // if (this.state.checked) {
        //     this.alarm(newAlarmState)
        //     this.Timer.forEach((t)=> clearInterval(t))
        //     Object.keys(alarmInRooms).map((key, index) => {
        //         if (alarmInRooms[key]) {
        //             newAlarmState[key]=false
        //         }
        //     })
        //
        // }
        this.setState(prevState => ({
            ...prevState,
            rooms: rooms,
            alarmInRooms: alarmInRooms
        }))


    }
    activateAlarm= (checked) => {
        var ARooms = this.state.alarmInRooms
        if (checked) {
            Object.keys(this.Timer).forEach((room)=> {
                const roomElement = document.querySelector(`[data-name*="${room}"]`);
                if (roomElement.classList.contains('backgroundRed')) {
                    roomElement.classList.remove('backgroundRed');
                }
                clearInterval(this.Timer[room])
            })
            Object.keys(ARooms).forEach((i) => {
                this.Timer[i] = setInterval(function () {
                        const roomElement = document.querySelector(`[data-name*="${i}"]`);
                        roomElement.classList.toggle("backgroundRed")
                },1500)
            })
        }
    }

    alarm = (receivers) => {
        let Rooms = this.state.alarmInRooms
        Object.keys(Rooms).forEach((i) => {
            this.Timer.push(setInterval(function () {
                if (Rooms[i]) {
                    const roomElement = document.querySelector(`[data-name*="${i}"]`);
                    roomElement.classList.toggle("backgroundRed")
                }
            },1500))
        })

    };

    handleSwitchChange = () =>{

        debugger;
        if (!this.state.checked) {
            this.activateAlarm(!this.state.checked)
            // Object.keys(this.state.alarmInRooms).map((key, index) => {
            //     if (this.state.alarmInRooms[key]) {
            //         newAlarmState[key]=false
            //     }
            // })
        }
        else{
            Object.keys(this.Timer).forEach((room)=> {
                const roomElement = document.querySelector(`[data-name*="${room}"]`);
                if (roomElement.classList.contains('backgroundRed')) {
                    roomElement.classList.remove('backgroundRed');
                }
                clearInterval(this.Timer[room])
            })        }

        this.setState({
            ...this.state,
            checked:!this.state.checked,
        })
    }
    useStyles = () =>{
        makeStyles((theme) => ({
            root: {
                width: '100%',
                maxWidth: '36ch',
                backgroundColor: theme.palette.background.paper,
            },
            inline: {
                display: 'inline',
            },
        }));
    }
    render(){
        const classes = this.useStyles();

        return(
            <>
                <div>
                    <FormControlLabel
                        control={<Switch checked={this.state.checked} onChange={this.handleSwitchChange} name="checkedA" />}
                        label="Alert Mode"
                    />
                    <Mapimg />
                    {this.props && this.props.data && this.props.data.length && this.props.data.map(obj=> {

                        return <Marker key={obj.id_number} color='blue' x={obj.x} y={obj.y} name={obj.room_num+" "} id={obj.id_number + '' } receiver_desc={obj.receiver_desc+''} receiver_id={obj.receiver_id+''} badge={obj.badge_type_desc+''}  />
                        }
                    )}
                    {Object.keys(this.state.rooms).map((key, index) => {

                    })}

                    <List className={classes.root}>
                        <ListItem alignItems="flex-start">
                            <ListItemAvatar>
                                <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
                            </ListItemAvatar>
                            <ListItemText
                                primary="Brunch this weekend?"
                                secondary={
                                    <React.Fragment>
                                        <Typography
                                            component="span"
                                            variant="body2"
                                            className={classes.inline}
                                            color="textPrimary"
                                        >
                                            Ali Connors
                                        </Typography>
                                        {" — I'll be in your neighborhood doing errands this…"}
                                    </React.Fragment>
                                }
                            />
                        </ListItem>
                        <Divider variant="inset" component="li" />
                        <ListItem alignItems="flex-start">
                            <ListItemAvatar>
                                <Avatar alt="Travis Howard" src="/static/images/avatar/2.jpg" />
                            </ListItemAvatar>
                            <ListItemText
                                primary="Summer BBQ"
                                secondary={
                                    <React.Fragment>
                                        <Typography
                                            component="span"
                                            variant="body2"
                                            className={classes.inline}
                                            color="textPrimary"
                                        >
                                            to Scott, Alex, Jennifer
                                        </Typography>
                                        {" — Wish I could come, but I'm out of town this…"}
                                    </React.Fragment>
                                }
                            />
                        </ListItem>
                        <Divider variant="inset" component="li" />
                        <ListItem alignItems="flex-start">
                            <ListItemAvatar>
                                <Avatar alt="Cindy Baker" src="/static/images/avatar/3.jpg" />
                            </ListItemAvatar>
                            <ListItemText
                                primary="Oui Oui"
                                secondary={
                                    <React.Fragment>
                                        <Typography
                                            component="span"
                                            variant="body2"
                                            className={classes.inline}
                                            color="textPrimary"
                                        >
                                            Sandra Adams
                                        </Typography>
                                        {' — Do you have Paris recommendations? Have you ever…'}
                                    </React.Fragment>
                                }
                            />
                        </ListItem>
                    </List>
                </div>
            </>
        )
    }
}
