import React, { Component } from 'react'
import './Map.css';
import Marker from '../Markers/Marker'
import { ReactComponent as Mapimg } from './6thFloorMap15.svg'

export default class extends Component{
    constructor(props){
        super(props);
        this.state={
            marks:''
        }
    }
    getPosition = (receiver) => {
        const roomElement = document.querySelector(`[data-name*="${receiver}"]`);
        let x = 0, y =0 ;
        if (roomElement) {
            const rect = roomElement.getBoundingClientRect();
            x = Math.random() * (rect.right - rect.left) + rect.left + 15;
            y = Math.random() * (rect.bottom - rect.top) + rect.top -10;
        }
        return {x, y}
    };

    render(){

        return(
            <>
                <Mapimg />
                {this.props && this.props.data && this.props.data.length && this.props.data.map(obj=> {
                    let pos=this.getPosition(obj.receiver_id)
                    return <Marker key={obj.id_number} color='blue' x={pos.x} y={pos.y} name={obj.room_num+" "} id={obj.id_number + '' } receiver_id={obj.receiver_id} badge={obj.badge_type_desc} />
                    })
                }
            </>
        )
    }
}
