import React, { Component } from 'react'
import './Map.css';
import Marker from '../Markers/Marker'
import { ReactComponent as Mapimg } from './6thFloorMap15.svg'
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Switch from '@material-ui/core/Switch';

export default class extends Component{
    constructor(props){
        super(props);
        this.state= {
            rooms: {},
            alarmInRooms: {},
            checked:false,
        }
        this.Timer = null;

    }
    componentDidUpdate(prevProps) {
        // Typical usage (don't forget to compare props):
        const rooms={}
        const alarm={}
        if (this.props.data !== prevProps.data) {
            this.props.data.forEach(obj => {
                rooms[obj.receiver_id] = rooms[obj.receiver_id] ? [...rooms[obj.receiver_id], obj] : [obj]
                if (rooms[obj.receiver_id].length >=2){
                    alarm[obj.receiver_id]=true
                }
            }
            )
            this.checkIn(rooms,alarm)
        }
    }

    checkIn=(rooms,alarmInRooms) => {
        // Redundunt
        const newAlarmState=alarmInRooms
        if (this.state.checked) {
            clearInterval(this.Timer)
            Object.keys(alarmInRooms).map((key, index) => {
                if (alarmInRooms[key]) {
                    newAlarmState[key]=false
                    this.alarm(key)
                }
            })
        }
        this.setState(prevState => ({
            ...prevState,
            rooms: rooms,
            alarmInRooms: alarmInRooms
        }))


    }
    alarm = (receiver) => {
        const roomElement = document.querySelector(`[data-name*="${receiver}"]`);

        if (roomElement) {
            this.Timer = setInterval(function(){roomElement.classList.toggle("backgroundRed")},1500);
        }
    };

    handleSwitchChange = () =>{
        debugger;
        const newAlarmState=this.state.alarmInRooms
        if (!this.state.checked) {
            Object.keys(this.state.alarmInRooms).map((key, index) => {
                if (this.state.alarmInRooms[key]) {
                    newAlarmState[key]=false
                    this.alarm(key)
                }
            })
        }
        else{
            clearInterval(this.Timer);
        }
        this.setState({
            ...this.state,
            alarmInRooms:newAlarmState,
            checked:!this.state.checked,
        })
    }
    render(){

        return(
            <>
                <FormControlLabel
                    control={<Switch checked={this.state.checked} onChange={this.handleSwitchChange} name="checkedA" />}
                    label="Secondary"
                />
                <Mapimg />
                {this.props && this.props.data && this.props.data.length && this.props.data.map(obj=> {

                    return <Marker key={obj.id_number} color='blue' x={obj.x} y={obj.y} name={obj.room_num+" "} id={obj.id_number + '' } receiver_desc={obj.receiver_desc+''} receiver_id={obj.receiver_id+''} badge={obj.badge_type_desc+''}  />
                    }
                )}
            </>
        )
    }
}
